<script>
export default {
  props: ["duration", "currentTime", "pause", "isBools"],
  data: function () {
    return {
      minute: 0,
      second: 0,
      newMinute: 0,
      newSecond: 0,
      isBool: true,
      switchover: 1,
    };
  },
  methods: {
    drag: function (e) {
      this.$emit("update:drag", e.target.value);
    },
  },
  updated() {
    this.minute = Math.floor(this.duration / 60);
    this.second = Math.floor(this.duration % 60);
    this.newMinute = Math.floor(this.currentTime / 60);
    this.newSecond = Math.floor(this.currentTime % 60);
  },
};
</script>

